model HydrologyModel

global {
  int nb_cells <- 100;
  geometry shape <- square(100);
  init {
    create cells number: nb_cells {
      location <- one_of(shape);
      water_level <- rnd(100);
    }
  }
}

species cells {
  float water_level;
  aspect base {
    draw square(1) color: rgb(0, 0, water_level);
  }
  reflex flow {
    float total_water = sum_of(cells neighbors_at: 1, each.water_level);
    float avg_water = total_water / count(cells neighbors_at: 1);
    if (water_level > avg_water) {
      float flow = (water_level - avg_water) / 2;
      water_level <- water_level - flow;
      ask cells neighbors_at: 1 {
        water_level <- water_level + flow / count(cells neighbors_at: 1);
      }
    }
  }
}

