grid cells width: grid_size height: grid_size neighbors: 8 {
  float water_level;
  reflex diffuse {
    float total_water = sum_of(cells neighbors_at: 1, each.water_level);
    float avg_water = total_water / count(cells neighbors_at: 1);
    float diff = (water_level - avg_water) * diffusion_rate;
    water_level <- water_level - diff;
    ask cells neighbors_at: 1 {
      water_level <- water_level + diff / count(cells neighbors_at: 1);
    }
  }
  aspect base {
    draw square(1) color: rgb(0, 0, water_level);
  }
}

