		geometry shape <- square(40);

		reflex move {
			do wander amplitude: 2.0;
			loop s over: terrain cells_in self {
				terrain[geometry(s).location] <- terrain[geometry(s).location] + 1.0;
			}

		}
	}

	species decreaser skills: [moving] {
		geometry shape <- square(40);

		reflex move {
			do wander amplitude: 2.0;
			loop s over: terrain cells_in self {
				terrain[geometry(s).location] <- terrain[geometry(s).location] - 1.0;
			}

		}

	}

}

