		 }


	}

}

action treePlanting(int no_ttb_planted, parcel destination_parcel){
geometry available_space; 
		ask destination_parcel{
			geometry sh <- shape;
			ask parcel_trees {
				geometry occupied_spaces <- circle(self.dbh #cm) translated_to self.location;
				 sh <- sh - occupied_spaces;
			}
			available_space <- sh;
		}


		create trees number: no_ttb_planted{
			dbh <- 0.5;	
			th <- 0.3;	
			location <- any_location_in(available_space);	
			//location <- any_location_in(myself.my_parcel.shape);//same as above
			myself.destination_parcel.parcel_trees << self;	
			geometry circle_tree <- circle(self.dbh #cm) translated_to self.location;
			available_space <- available_space - circle_tree;
		}
}

