species myagent {
float opinion min:-1 max:1;
float confidence min:-1 max:1;

reflex opinion_diffusion {
	ask any(myagent-self) { 
		if abs(opinion-myself.opinion) < myself.confidence {
			myself.opinion <- myself.opinion * (1-delta) + opinion * (delta); 
		}
	}
}

}

