grid life_cell width: environment_width height: environment_height neighbors: 8 {
	bool new_state;
	list<life_cell> neighbours <- self neighbors_at 1;
	bool alive <- (rnd(100)) < 80;

	rgb color <- alive ? livingcolor : deadcolor;

	action evolve {
		int living <- neighbours count each.alive;
		if alive {
			new_state <- living in living_conditions;
			color <- new_state ? livingcolor : dyingcolor;
		} else {
			new_state <- living in birth_conditions;
			color <- new_state ? emergingcolor : deadcolor;
		}

	}
	reflex update {
		alive <- new_state;
	}

}

/**
* Name: BoundedConfidence
* Model proposed in Deffuant, G., Neau, D., Amblard, F., & Weisbuch, G. (2000). Mixing beliefs among interacting agents. Advances in Complex Systems, 3(01n04), 87-98. 
* Tags: social influence, bounded conficence
*/

model BoundedConfidence

global {
	float mu <- 0.5; //convergence parameter
	float d <- 0.5; //openness to discussion

	init {
		create people number: 100;
	}

}

species people {
	float opinion <- rnd(1.0);

	reflex interaction {
		ask one_of (people - self) {
			if abs(opinion - myself.opinion) < d {
				opinion <- opinion + mu * (myself.opinion - opinion);
				myself.opinion <- myself.opinion + mu * (opinion - myself.opinion);
			} 
		}
	}
}

experiment run type: gui {
	output {
		display opinion_evolution {
			chart "opinion evolution" {
				loop p over: people {
					data string(int(p)) value: p.opinion;
				}
			}
		}
	}
}

model fire_spreading

global {
	int fire_time <- 5;
	float proba_fire <- 0.2;

	init {
		ask cell(location) {
			do to_fire;
		}
	}
}

grid cell width: 50 height: 50 schedules: cell where each.on_fire{
	bool on_fire <- false;
	int fire_counter;

	action to_fire {
		on_fire <- true;
		color <- #red;
		fire_counter <- fire_time;
	}

	reflex spread when: on_fire {
		ask neighbors where (each.color = #white){
			if flip(proba_fire) {
				do to_fire;
			}
		}
		fire_counter <- fire_counter - 1;

	}

	reflex end_of_fire when: on_fire and fire_counter = 0{
		on_fire <- false;
		color <- #gray;
	}
}

experiment fire type: gui {
	output {
		display map {
			grid cell border: #black;
		}
	}
}

